private void comAceptarActionPerformed(java.awt.event.ActionEvent evt) {                                           
    // TODO add your handling code here:
    String nombre = txtNombre.getText();
    if ( nombre.length() > 0 ){
        String inicio = OperacionesDate.cadenaDate(datFechaInicio.getDate());
        String fin = OperacionesDate.cadenaDate(datFechaFin.getDate());
        String anualidad = String.valueOf(datYear.getValue());   
        try {
            FormPrincipal.getCon().ejecutaStatement("insert into temporada(nombre,fechaini,fechafin,ano) values('" + nombre + "','" + inicio + "','" + fin + "','"+anualidad+"')");
        } catch (Exception e) {
            
        }
   
        this.setVisible(false);
    }else{
        javax.swing.JOptionPane.showMessageDialog(null, "Debe Ingresar un nombre para la temporada");
    }
   
}                                                                            

